import { Injectable } from '@angular/core';
import { IBook } from '../types/IBook';

@Injectable({
  providedIn: 'root'
})
export class CartService {

  cart : IBook[] = [];

  constructor() { }

  add(book:IBook){
    console.log(book);
    this.cart.push(book)
  }

  get(){
    return this.cart;
  }
}
